let defaultStates = {
    menu: "Mind",
};

const changeStateReducer = (state = defaultStates, action) => {
    switch (action.type) {
        case "Menu":
            if (action.state === "Setting") {
                return { menu: action.state };
            } else {
                return { menu: state.menu };
            }

        default:
            break;
    }
};

export default changeStateReducer;
