const changePathReducer = (
state = { path: "Home", session: "25", break: "5" },
    action
) => {
    switch (action.type) {
        case "Path":
            if (action.state === "Setting") {
                return { path: action.state };
            } else {
                return { path: action.state };
            }
        case "session":
                if (action.state === "increment"){
                   return {session : state.session + 1} 
                } else if (action.state === "decrement"){
                    return {session : state.session - 1}
                }
        case "break":
                if (action.state === "increment"){
                   return {break : state.break + 1} 
                } else if (action.state === "decrement"){
                    return {break : state.break - 1}
                }

        default:
            return { path: state.path ,session : state.session , break: state.break };
    }
};

export default changePathReducer;
