import React, { useState, useEffect } from "react";
import "./index.css";

export default function Main() {
  const [mind, setmind] = useState("Focus");
  const [pervMind, setPervMind] = useState("Focus");
  const [sessions, setsessions] = useState(4);
  const [second, setseconds] = useState(0);
  const [miniSecond, setMiniSecond] = useState(10);
  const [minute, setminute] = useState(10);
  let [timer, settimer] = useState(true);
  var [date, setdate] = useState(Date.now());
  let [a, seta] = useState("");
  let [b, setb] = useState("");
  if (timer === false) {
    setTimeout(() => {
      if (sessions > 0) {
        if (second === 0 && minute > 0) {
          setminute(minute - 1);
          setseconds(59);
        } else if (second > 0 && minute >= 0) {
          seta(((Date.now() - date) / 1000).toFixed());
          setb(a);
          if (a !== b) {
            setseconds(second - 1);
          }
        } else if (minute === 0 && second === 0) {
          if (mind === "Focus" || pervMind === "Focus") {
            setminute(5);
            setmind("chill");
          } else {
            if (sessions > 1) {
              setminute(10);
              setmind("Focus");
              setsessions(sessions - 1);
            } else {
              setsessions(sessions - 1);
              setmind("bass karo");
            }
          }
        }
      }
    }, 1000);
  }

  return (
    <div className="main">
      <div>
        <button>
          <i class="fa-solid fa-hat-cowboy"></i>
          SIGN IN
        </button>
      </div>
      <div>
        <button>
          <i class="fa-regular fa-circle-right"></i>
          ADD LABEL
        </button>
      </div>
      <div>
        <div class="circular">
          <div class="inner"></div>
          <div class="number">
            <div style={{ marginTop: "o.8vh" }}>
              {minute > 9 ? minute : "0" + minute}:
              {second > 9 ? second : "0" + second}
            </div>
            <div style={{ fontSize: "7px", marginTop: "0.5vh" }}>{mind}</div>
          </div>
          <div class="circle">
            <div class="bar left">
              <div class="progress"></div>
            </div>
            <div class="bar right">
              <div class="progress"></div>
            </div>
          </div>
        </div>
      </div>
      <div className="audioplayer">
        <span style={{ marginTop: "1.5vh" }}>
          <a
            onClick={() => {
              setsessions(4);
              setminute(10);
              setseconds(0);
              setmind("Focus");
              settimer(true);
            }}
          >
            <i
              style={{ fontSize: "4vh", color: "#C1C1C1" }}
              class="fa-solid fa-arrow-rotate-right"
            ></i>
          </a>
        </span>
        <span>
          <a
            onClick={() => {
              if (timer === true) {
                settimer(false);
                if (minute !== 0 && second !== 0) {
                  setmind(pervMind);
                }
              } else {
                settimer(true);
                setPervMind(mind);
                setmind("Stop");
              }
            }}
          >
            <i
              style={{
                fontSize: "7vh",
                color: "#4158D0",
                marginRight: "1.5vw",
                marginLeft: "1.5vw",
              }}
              class="fa-regular fa-circle-play"
            ></i>
          </a>
        </span>
        <span style={{ marginTop: "1.5vh" }}>
          <a
            onClick={() => {
              setTimeout(() => {
                setseconds(0);
                setminute(0);
              }, 1000);
              settimer(true);
              if (mind !== "Stop") {
                setmind("Stop");
                setPervMind(mind);
              }
            }}
          >
            <i
              style={{ fontSize: "4vh", color: "#C1C1C1" }}
              class="fa-solid fa-forward-step"
            ></i>
          </a>
        </span>
      </div>
      <div>
        <p>
          {sessions} of 4<br></br>sessions
        </p>
      </div>
      <div>Made with love by Huraira Shahid</div>
      <div className="social">
        <span style={{ marginRight: "5px" }}>
          <i class="fa-brands fa-twitter"></i>
        </span>
        <span>
          <i class="fa-brands fa-github"></i>
        </span>
      </div>
    </div>
  );
}
