const changePathReducer = (state = { path: "Home" }, action) => {
    switch (action.type) {
        case "path":
            if (action.state === "Setting") {
                return { path: action.state };
            } else {
                return { path: state.path };
            }

        default:
            break;
    }
};

export default changeStateReducer;
